import React, { useContext, useState, useEffect } from "react";
import FirebaseContext from './Firebase'
import AuthContext from './Firebase/AuthContext'
import { Link, useHistory } from 'react-router-dom'
import { Formik, Form, Field } from 'formik'
import { Card } from 'react-bootstrap'

function Friends() {
    const firebase = useContext(FirebaseContext)
    const [user, setUser] = useState()
    const [friends, setFriends] = useState([])
    const [loading, setLoading] = useState(true)

    firebase.auth.onAuthStateChanged(user => {
        if (user) {
            setUser(user);
        } else {
            setUser(null)
        }
    });

    useEffect(() => {
        if (user) {
            let friendList
            let friendsBuilder = []
            let count = 0
            firebase.db.collection('user').doc(user.uid).get().then(function (doc) {
                friendList = doc.data().friends
            }).then(function () {
                friendList.forEach((friend, index) => {
                    firebase.db.collection('user').doc(friend).get().then(function (doc) {
                        friendsBuilder.push(doc.data())
                        count++
                        if (count === friendList.length) {
                            setLoading(false)
                        }
                    })
                })
                setFriends(friendsBuilder)
            })
        }
    }, [user])

    // let localDisplay = 'loading...'

    // if (!loading) {
    //     // console.log(friends)
    //     localDisplay = friends.map((friend, index) => {
    //         return (
    //             <Card>
    //                 <Card.Title>{friend.name}</Card.Title>
    //                 <p>{friend.insta && friend.insta}</p>
    //                 <p>{friend.snap && friend.snap}</p>
    //             </Card>
    //         )
    //     })
    // }


    return (
        <>
            <Formik
                initialValues={{ username: '', password: '' }}
                onSubmit={async (values, { setSubmitting }) => {
                    try {
                        //wait until log in is complete
                        await firebase.auth.signInWithEmailAndPassword(values.username, values.password)
                        // history.push('/')
                    } catch (error) {
                        alert(error)
                    }
                }}
            >
                <Form className="login-form">
                    <Field as="input" type="text" placeholder="username" name="username" />
                    <Field as="input" type="password" placeholder="password" name="password" />
                    <button type="submit">login</button>
                    <p className="message">Not registered? <Link to="/register">Create an account</Link></p>
                </Form>
            </Formik>
        </>
    )
}

export default Friends
