import React, { useContext, useEffect, useState } from 'react';
import FirebaseContext from '../Firebase';
import Title from './ChatBoxComponent/Title';
import Cell from './ChatBoxComponent/Cell';

var arrayMessages = [];
function Chatbox() {
	/*
	constructor(props) {
		super(props);

		this.state = {
			chatID: 'T5O5rvqkXjOTTNzgClyK',
			chats: [],
		};

		this.getData = this.getData.bind(this);
    }
    */
	const [chatID, setChatID] = useState('T5O5rvqkXjOTTNzgClyK');
	const [chats, setChats] = useState(['']);
	const [isLoading, setIsLoading] = useState(true);
	const firebase = useContext(FirebaseContext);
	const chatMessages = firebase.db.collection('messages').doc(chatID);

	function getData() {
		chatMessages
			.get()
			.then(function (doc) {
				if (doc.exists) {
					arrayMessages = Object.values(doc.data()).sort((a, b) =>
						a[0] > b[0] ? 1 : -1
					);
					setIsLoading(false);
				} else {
					// doc.data() will be undefined in this case
					console.log('No such document!');
					return [];
				}
			})
			.catch(function (error) {
				console.log('Error getting document:', error);
			});
	}

	useEffect(() => {
		getData();
	}, []);

	return (
		<div>
			<Title imgUrl='' userName='Sally' />
			{isLoading ? (
				<p>is loading...</p>
			) : (
				arrayMessages.map((message) => (
					<Cell avatar={''} sender={message[2]} message={message[3]} />
				))
			)}
		</div>
	);
}

export default Chatbox;
