import React, { useContext, useState, useEffect } from "react";
import FirebaseContext from './Firebase'
import AuthContext from './Firebase/AuthContext'
import { Link, useHistory } from 'react-router-dom'
import { Formik, Field } from 'formik'
import { Card, Form } from 'react-bootstrap'

function Genre() {
    const firebase = useContext(FirebaseContext)
    const [user, setUser] = useState()
    const [genres, setGenres] = useState([])
    const [loading, setLoading] = useState(true)

    firebase.auth.onAuthStateChanged(user => {
        if (user) {
            setUser(user);
        } else {
            setUser(null)
        }
    });

    useEffect(() => {
        if (user) {
            let friendList
            let friendsBuilder = []
            let count = 0
            firebase.db.collection('user').doc(user.uid).get().then(function (doc) {
                friendList = doc.data().friends
            }).then(function () {
                friendList.forEach((friend, index) => {
                    firebase.db.collection('user').doc(friend).get().then(function (doc) {
                        friendsBuilder.push(doc.data())
                        count++
                        if (count === friendList.length) {
                            setLoading(false)
                        }
                    })
                })
                // setFriends(friendsBuilder)
            })
        }
    }, [user])

    let genreList = ['Action', 'Adult', 'Adventure', 'Animation', 'Biography', 'Comedy', 'Crime', 'Documentary', 'Drama', 'Family', 'Fantasy', 'Film Noir', 'Game Show', 'History', 'Horror', 'Musical', 'Music', 'Mystery', 'News', 'Reality TV', 'Romance', 'Sci Fi', 'Short', 'Sport', 'Talk Show', 'Thriller', 'War', 'Western']
    let checkingField = genreList.map(genre => {
        return (
            <Form.Check
                type={'checkbox'}
                id={genre}
                label={genre}
                checked={genres.includes(genre)}
                onChange={genres.includes(genre) ? () => {
                    setGenres(genres.filter(filter => filter !== genre))
                } : () => {
                    setGenres(genres.concat(genre))
                }}
            />
        )
    })


    return (
        <div>
            <Form id="genre">
                {checkingField}
                {/* <Form.Check
                    type={'checkbox'}
                    id={'act ion'}
                    label={'Action'}
                    checked={genres.includes('action')}
                    onChange={genres.includes('action') ? () => {
                        setGenres(genres.filter(filter => filter !== 'action'))
                    } : () => {
                        setGenres(genres.concat('action'))
                    }}
                /> */}

            </Form>

        </div>
    )
}

export default Genre
