// function AddMovie(movieIMDB) {
//     const firebase = useContext(FirebaseContext)
//     firebase.collection("movieTester").doc("testUser").update({
//         regions: firebase.firestore.movieID.arrayUnion(movieIMDB)
//     })
// }

class MovieFinder extends React.Component {
    constructor() {
        super()
        this.state = {
            movieName: '',
            upload: false,
            title:'',
            genre:'',
            year:'',
            director:'',
            length:'',
            plot:'',
            resultID: '',
            img: '',
            data: '',
            response: false
        }
        const firebase = useContext(FirebaseContext)
        this.nameInput = this.nameInput.bind(this)
        this.handleSearch = this.handleSearch.bind(this)
        this.submit = this.submit.bind(this)
    }

    nameInput(event) {
        console.log("updating name field")
        this.setState({
            movieName: event.target.value.trim()
        })
    }

    handleSearch() {
        console.log("http://www.omdbapi.com/?t=" + this.state.movieName + "&apikey=" + API_KEY)
        fetch("http://www.omdbapi.com/?t=" + this.state.movieName + "&apikey=" + API_KEY)
            .then(response => 
                response.json())

            .then(data => {this.setState({
                response: data.Response === "False" ? false : true,
                title: data.Title,
                year: data.Year,
                length: data.Runtime,
                director: data.Director,
                genre: data.Genre,
                plot: data.Plot,
                resultID : data.imdbID,
                img : data.Poster
            })
            console.log("finished search")})
            console.log("result:" + this.state.searchResult)
    }

    submit() {
        this.firebase.db.collection("movieTester").doc("testUser").update({
            regions: this.firebase.firestore.movieID.arrayUnion(this.state.resultID)
        })
    }

    render() {
        return(
            <div>
                <form>
                    <input type="text" placeholder="Add another movie" onChange = {this.nameInput} />
                </form>

                <button onClick = {this.handleSearch}>Click to search</button>
                <button onClick = {this.submit}>Add to your collection!</button>
                <br/>

                <p style={{visibility: this.state.response ? "visible" : "hidden"}, {textAlign: 'left'}}>
                    <img src = {this.state.img} ></img>
                    <h1>{this.state.response && this.state.title}</h1>
                    {this.state.response && "Year: " + this.state.year}<br/>
                    {this.state.response && "Length: " + this.state.length}<br/>
                    {this.state.response && "Director: " + this.state.director}<br/>
                    {this.state.response && "Genre: " + this.state.genre}<br/>
                    {this.state.response && this.state.plot}<br/>
                </p>

            </div>
        )
    }
}

export default MovieFinder
