// function AddMovie(movieIMDB) {
//     const firebase = useContext(FirebaseContext)
//     firebase.collection("movieTester").doc("testUser").update({
//         regions: firebase.firestore.movieID.arrayUnion(movieIMDB)
//     })
// }

class MovieFinder extends React.Component {
    constructor() {
        super()
        this.state = {

            upload: false,
            searchResult: '',
            resultID: '',
            img: ''
        }
    }

    handleSearch(event) {
        console.log("http://www.omdbapi.com/?t=" + event.target.value + "&apikey=" + API_KEY)
        fetch("http://www.omdbapi.com/?t=" + event.target.value + "&apikey=" + API_KEY)
            .then(response => 
                response.json())

            .then(data => {this.setState = {
                searchResult : data.Title + " \nYear:" 
                    + data.year + " \nLength: " + data.Runtime + 
                    "\nGenre: " + data.Genre + 
                    "\n" + data.Plot,
                resultID : data.imdbID,
                img : data.img
            }
            })
    }

    render() {
        return(
            <div>
                <input type="text" placeholder="Add another movie"></input>
                <button onClick = {this.handleSearch}>Click to search</button>
                <button onClick = {this.submit}>Add to your collection!</button>
                <br/>
                <p>{this.searchResult}</p>
                <img src = {this.state.img}></img>
            </div>
        )
    }
}

export default MovieFinder
