import React from 'react';
import Post from './post.js';
import firebase from '../Firebase/firebase';

function CommunityPost(props){

  //props: community id to search for all the posts from database

  //array we get from databse
  let DummyPosts = [{username: "user1", content: "hi"},{username: "user2", content: "hi2"}];

  const [currentPost, setCurrentPost] = React.useState({
    username: "",
    content: ""
  });



  const [posts, setPosts] = React.useState(DummyPosts);

   function handlePostChange(event){
     const {name, value} = event.target;
     setCurrentPost(prevPost => {
       return {
         username: prevPost.username,
         content: value

       };
     });
   }

   function addPost(event){
     setPosts(prevPosts => {
       return [...prevPosts, currentPost]
     });

     setCurrentPost({
       username: "",
       content: ""
     });

     firebase.firestore().collection('posts').add({
       username: "testD1", content: "testD1"
     });


     event.preventDefault();
   }

  return(
    <div>
    <h1>Movie title</h1>
       <img src="" alt="User Icon"></img>
    <form>
        <button type="submit" onClick={addPost}>reply</button>
    </form>

    <input type="text" name="post" onChange={handlePostChange} placeholder="reply"></input>
    <h1>{currentPost.content}</h1>

    //post all the posts
    {posts.map(post => {
         return (
           <Post
            username={post.username}
            content={post.content}
          />
       )
    })}
    </div>
  );
}

export default CommunityPost;
