import React from 'react';
import Post from './post.js';

function CommunityPost(props){

  //props: community id to search for all the posts from database
  let posts = [{username: "user1", content: "hi"},{username: "user2", content: "hi2"}];
  const [currentPost, setCurrentPost] = useState({
    username: "",
    content: ""
  });
  //const [allPosts, setPosts] = useState([{}]);

   function handlePostChange(event){
     const {name, value} = event.target;
     setCurrentPost(prevPost => {
       return {
         username: prevPost.username,
         content: value

       };
     });

   }

  return(
    <div>
    <img src="" alt="Community Icon"></img>
    <h1>{props.communityName}</h1> // community name
    <button onClick={addPost} >New Post</button>
    <input type="text" name="post" onChange={handlePostChange} placeholder="add a new Post"></input>
    <h1>{currentPost}</h1>

//post all the posts
    {posts.map(post => {
         return (
           <Post
            username={post.username}
            content={post.content}
          />
       )
    })}
    </div>
  );
}

export default CommunityPost;
