import React, {useState, useEffect, useContext} from 'react'
import FirebaseContext from './Firebase'
import { Card,ListGroup,ListGroupItem, Button } from 'react-bootstrap'

const API_KEY = "7d667341"

function DailyRec(props) {
    let [movieData, setMovieData] = useState({})
    const [user, setUser] = useState()
    const [genreList, setGenreList] = useState([])
    const [loading, setLoading] = useState(true)
    const firebase = useContext(FirebaseContext)

    firebase.auth.onAuthStateChanged(user => {
        if (user) {
            setUser(user);
        } else {
            setUser(null)
        }
    });

    useEffect(() => {
        if (user){
            firebase.db.collection('user').doc(user.uid).get().then(function(doc){
                setGenreList(doc.data().genres)
                setLoading(false)
            })
        }
    }, [user])

    function arrayMatch(arr1, arr2){
        let returnVal = false
        arr1.forEach(element => {
            if (arr2.includes(element)){
                returnVal = true
                return
            }
        })
        return false
    }

    async function matching() {
        let match = false
        console.log("enter while loop")
        while(true) {
            let randMovieID = ''
            let i = 0
            for(i = 0; i < 7; i++) {
                randMovieID += Math.round(Math.random() * 10).toString()
            }
            let url = "http://www.omdbapi.com/?i=tt" + randMovieID + "&apikey=" + API_KEY
            let response = await fetch(url)
            let data = await response.json()
            console.log(data)
            if (data.Response === "False"){
                continue
            }
            let filmGenres = data.Genre.split(', ')
            console.log('fg', filmGenres)
            if(arrayMatch(filmGenres, genreList)) {
                setMovieData(data)
                break
            }
                // .then(response =>
                //     response.json())
                // .then(data => {setMovieData({
                //     movieData: data.Response && data.genre.includes(userGenre) && data
                //     })})
            console.log("finish one loop")
        }
    }

    return (
        <div>
          {/* <h1>{movieData == undefined ? "Loading today's recommendation" : ''}</h1> */}
          <h2>Get a movie recommendation based on your preferred genre - </h2>
          <Card style={{ width: '25rem' }}  className="center-item">

            <Card.Img variant="top" src={movieData.Poster} />

            <Card.Body>
                  <Card.Title>{movieData.Title}</Card.Title>
                  <Card.Text>
                    {"Length: " + movieData.Length}
                  </Card.Text>
            </Card.Body>

            <ListGroup className="list-group-flush">
              <ListGroupItem>{"Director: " +movieData.Director}</ListGroupItem>
              <ListGroupItem>{"Plot: " +movieData.Plot}</ListGroupItem>
            </ListGroup>
          </Card>

            <Button onClick={matching} disabled={loading}>Feeling lucky!</Button><br/>
            <label>Note: if the number of your preferred genres is smaller, it might take a while to load</label>
        </div>
    )
}

export default DailyRec
