import React, { useContext, useEffect, useState } from 'react';
import FirebaseContext from '../../Firebase';

function TextBox(props) {
	const [messageContent, setMessageContent] = useState('');
	const [messageSent, setMessageSent] = useState(false);
	const chatID = props.chatID;
	const user = props.user;
	const numMessage = props.numMessage;
	var allMessages = props.allMessages;
	const firebase = useContext(FirebaseContext);
	var d = new Date();

	function handleChange(event) {
		const { name, value } = event.target;
		name === 'messageSent' ? setMessageSent(true) : setMessageContent(value);
	}

	useEffect(() => {
		if (messageSent === true) {
			if (Object.keys(allMessages).length > 25) {
				var newAllMessages = {};
				var i = 0;
				for (i = 0; i < Object.keys(allMessages).length - 1; i++) {
					newAllMessages[i] = allMessages[i + 1];
				}
				allMessages = newAllMessages;
			}
			allMessages[Object.keys(allMessages).length] = [
				d.getTime(),
				user,
				messageContent,
			];
			firebase.db
				.collection('messages')
				.doc(chatID)
				.set({
					allMessages,
				})
				.then(function () {
					console.log('Document successfully written!');
				})
				.catch(function (error) {
					console.error('Error writing document: ', error);
				});

			setMessageContent('');
			setMessageSent(false);
		}
	}, [messageSent]);

	return (
		<div>
			<input
				type='text'
				value={messageContent}
				name='messageContent'
				placeholder='Type your message...'
				onChange={handleChange}
			/>
			<button type='button' name='messageSent' onClick={handleChange}>
				send
			</button>
		</div>
	);
}

export default TextBox;
