import React, {useState, useEffect} from 'react'



function MovieRec() {
    //userGenreList = props.userGenreList
    const userGenreList = ['romantic', 'adventure', 'sci-fi', 'suspense', 'anime']
    let userMovieList = ['tt4566758','tt6723592','tt8946378']
    let counter = 0
    // const [finished, setFinished] = useState(false)
    const [omdbData, setOmdbData] = useState('Loading')
    let textToBeDisplayed = userGenreList[Math.round(Math.random() * userGenreList.length)]
    let url = "http://www.omdbapi.com/?i=" + userMovieList[counter].toString() + "&apikey=906b7c64"
    useEffect(() => {
        fetch(url)
            .then(response => 
                response.json())

            .then(data => {
                setOmdbData(data)
            })
    }, [])


    let title = omdbData === 'Loading' ? omdbData : omdbData.Title
    let genre = omdbData === 'Loading'? '' : "Genre: " + omdbData.Genre
    let director = omdbData === 'Loading'? '' : "Director: " + omdbData.Director
    let plot = omdbData === 'Loading' ? '' : "Plot: " + omdbData.Plot

    function buttonClick(counterChange) {
        if(counterChange > 0 || counter + counterChange >= userMovieList.length) counter = 0
        if(counterChange < 0 || counter + counterChange < 0) counter = userMovieList.length - 1
        console.log("counter is now" + counter)
    }

    return (
        <div>
            <button>Back</button>
            <button onClick = {buttonClick(1)}>Left</button>
            <button onClick = {buttonClick(-1)}>Right</button>
            <div>
                <h1>{title}</h1>
                <h3>{genre}</h3>
                <h3>{director}</h3>
                <p>{plot}</p>
            </div>

        </div>
    )

}

export default MovieRec
