import React, { useState, useContext} from "react";
import FirebaseContext from '../Firebase';


import Note from "./Note";
import CreateArea from "./CreateArea";




// props: database idto search for
function Home(props) {
  const id = "test-main";
  const firebase = useContext(FirebaseContext);

  firebase.db.collection("community-discussion-board").doc(id).set({
    comments: ["com1", "com2","com3","com4"]
  }).then(()=>{
    console.log();
  }).catch(err => console.log(err));

  var docRef = firebase.db.collection("community-discussion-board").doc(id);

docRef.get().then(function(doc) {
    if (doc.exists) {
        console.log("Document data:", doc.data());
    } else {
        // doc.data() will be undefined in this case
        console.log("No such document!");
    }
}).catch(function(error) {
    console.log("Error getting document:", error);
});








  const [notes, setNotes] = useState([]);



  function addNote(newNote) {
    setNotes(prevNotes => {
      return [...prevNotes, newNote];
    });
  }

  function deleteNote(id) {
    setNotes(prevNotes => {
      return prevNotes.filter((noteItem, index) => {
        return index !== id;
      });
    });
  }

  return (
    <div>
      <h1>Movie Title</h1>
      <p>content of the community post</p>

      <CreateArea onAdd={addNote} />
      {notes.map((noteItem, index) => {
        return (
          <Note
            key={index}
            id={index}
            title={noteItem.title}
            content={noteItem.content}
            onDelete={deleteNote}
          />
        );
      })}
    </div>
  );
}

export default Home;
